import React from 'react';

const YourBotArmy = ({ botArmy, onReleaseBot, onDischargeBot }) => {
  return (
    <div>
      <h2>Your Bot Army</h2>
      <div className="row">
        {botArmy.map((bot) => (
          <div key={bot.id} className="col-md-4">
            <div className="card border border-gray-300 rounded-lg p-4 mb-4">
              <img
                src={bot.avatar_url}
                alt={`Avatar of ${bot.name}`}
                className="card-img-top w-100"
              />
              <div className="card-body">
                <h2 className="card-title text-xl font-semibold">{bot.name}</h2>
                <p className="card-text text-gray-500">Class: {bot.bot_class}</p>
                <p className="card-text text-gray-500">Health: {bot.health}</p>
                <p className="card-text text-gray-500">Damage: {bot.damage}</p>
                <p className="card-text text-gray-500">Armor: {bot.armor}</p>
                <p className="card-text text-gray-500">Catchphrase: {bot.catchphrase}</p>

                <button
                  className="btn btn-danger font-semibold"
                  onClick={() => onReleaseBot(bot.id)}
                >
                  Release
                </button>
                <button
                  className="btn btn-danger font-semibold ml-2"
                  onClick={() => onDischargeBot(bot.id)}
                >
                  Discharge
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default YourBotArmy;
